def split(species):    cons = 'aeiou'    cnt = 0    for i in species:        # if cons.find(i.lower()) != -1:        if i.lower() in cons:            pre = species[:cnt]            suf = species[cnt:]            break        cnt += 1    return (pre, suf)def split2(species):    cons = 'aeiou'    cnt = 0    while cnt < len(species) and species[cnt].lower() not in cons:        cnt +=1    return (species[:cnt], species[cnt:])def hybridize(s1, s2):    aa = split2(s1)    bb = split2(s2)    s11 = aa[0]+bb[1]    s22 = bb[0]+aa[1]    return (s11, s22)if __name__ == '__main__':    import  doctest    doctest.testmod()# aa = hybridize('sheep','goat')aa = hybridize('Brad', 'Angelina')print(aa)