def isbn(code):    if len(code) == 10:        # print('test')        last_dig = sum((i+1)*int(code[i]) for i in range(9)) %11        if str(last_dig) == code[9] or (last_dig == 10 and code[9] == 'X'):            print("OK")        else:            print("WRONG")    else:        print('The format is not correct!')    returncode = input("Enter eh ISBN code ")while code != 'stop':    isbn(code)    code = input("Enter eh ISBN code ")