def kw1(label_):    instr = 'ABCDEFGHIJ'    outstr = '0123456789'    # str.maketrans(instr, outstr)    # instr.maketrans(outstr)    aa = 1 << ( int(label_.translate(str.maketrans(instr, outstr))))    # print(label_.translate(str.maketrans(instr, outstr)))    return aadef kw2():    # sum_ = 0    sum_ = sum(1<< ord(input('the prisoners label')) - ord('A') for _ in range(int(input('# of prisoners'))))    return sum_def run_func1():    label_num = input("poisoned prisoners")    n= 0    sum_ = 0    while n < int(label_num):        label_ = input("the prisoners label")        sum_ += kw1(label_)        n+=1    print("Bottle", '#'+str(sum_), "is poisoned.")    returnprint("Bottle", '#'+str(kw2()), "is poisoned.")