def missing_num(str_):    strCnt = 1    missing = None    while missing is None and strCnt <= len(str_) // 2:    	    	head = int(str_[:strCnt])    	reamaining = str_[strCnt:]    	while reamaining:  	    		head += 1	    		if  reamaining.startswith(str(head)):    			reamaining = reamaining[len(str(head)):]    		elif missing is None:    			missing = head    		else:    			reamaining = ''    			missing = None    	strCnt += 1			    return  missinga = input('input number series')return_ = missing_num(a)print(return_ if return_ is not None else 'no missing number')