def pitch_(cip):    pos = int (input("input the starting position"))    step = int(input("input the step size"))    # step = 0    depcipher = cip[pos]    # depcipher = sum (cip[(pos += step)] for _ in cip)    for _ in range(len(cip)-1):        pos += step        depcipher += cip[(pos%len(cip))]    print (depcipher)    returndef pitch_2 (cip):    pos = int(input())    step = int(input())    print(''.join(cip[(pos + i*step)% len(cip)] for i in range(len(cip)) ) )    returncip = input("input cipher")pitch_2(cip)