import  mathi=0def walk_str(pi_):    xx = sum(math.sin(math.radians(int(i)*36)) for i in pi_ if i.isdigit())    yy = sum(math.cos(math.radians(int(i)*36)) for i in pi_ if i.isdigit())    print_fun(pi_, xx, yy)    returndef walk_str2(pi_):    xx,yy = 0.00, 0.00    for i in pi_:        if i.isdigit():            xx += math.sin(math.radians(int(i)*36))            yy += math.cos(math.radians(int(i) * 36))    print_fun(pi_, xx, yy)    returndef print_fun(pi_,xx,yy):    template = 'Number {} walks to position ({:.2f}, {:.2f}).'    print (template.format(pi_, xx, yy ))walk_str2(input('input the pi value'))